import { useState } from "react";

function randomIntFromInterval(min, max) {
  // min and max included
  return Math.floor(Math.random() * (max - min + 1) + min);
}

export default function Students({ studentsList }) {
  console.log(studentsList);
  const [selectedStudents, setSelectedStudents] = useState([]);

  const raffleStudent = () => {
    let availableStudents = studentsList;
    if (selectedStudents.length > 0) {
      availableStudents = studentsList.filter((stu) => {
        return (
          selectedStudents.indexOf((seStu) => seStu.house === stu.house) === -1
        );
      });
    }
    return availableStudents[
      randomIntFromInterval(0, availableStudents.length - 1)
    ];
  };

  const raffle = () => {
    for (let i = 0; i < 3; i++) {
      const raffledStudent = raffleStudent();
      setSelectedStudents([...selectedStudents, raffledStudent]);
    }
    console.log(selectedStudents);
  };

  console.log(raffleStudent());

  return (
    <div>
      <button onClick={raffle}>Sortear estudantes</button>
      {selectedStudents.map((student) => {
        <span>{student.name}</span>;
      })}
    </div>
  );
}
